import React, { useRef, useEffect, useState, ReactComponentElement, ReactNode, useLayoutEffect } from 'react';
import ReactDOM from 'react-dom/client';
import './App.css';
import NavBar from './components/NavBar';
import { Box, Container } from '@mui/material';

import OuterContainer from './components/OuterContainer';
import { WidthWideOutlined } from '@mui/icons-material';

import ProfilePage from './components/ProfilePage';
import SwipePage from './components/SwipePage';
import MatchedProfiles from './components/MatchedProfiles';
import ErrorPage from './components/ErrorPage';
import CreateProfile from './components/CreateProfile';

type PageType = 'swipe' | 'profile' | 'matched';
interface UserProfile {
  firstName: string;
  lastName: string;
  gender: 'male' | 'female' | 'non-binary' | '';
  photos: string[];
  location: 3 | 4 | 5 | 0;
}

interface UserData {
  userProfile: UserProfile;
  setUserProfile: Function;
}

const pages: Record<string, JSX.Element> = {
  swipe: (
    <SwipePage cardData={'dummy'}/>
  ),
  profile: (
    <ProfilePage />
  ),
  matched: (
    <MatchedProfiles />
  )
};


function App() {
  const width = window.innerWidth > 992 ? 428 : '100%';
  const [isLoading, setLoading] = useState<boolean>(true);
  const dbName = 'userProfileDB';
  const objectStoreName = 'profiles';
  const dbVersion = 1;
  const [hasProfile, setHasProfile] = useState<boolean>(true);
  const [userProfile, setUserProfile] = useState<UserProfile>({
    firstName: '',
    lastName: '',
    gender: '',
    photos: [],
    location: 0,
  });
  const [objectStore, setObjectStore] = useState<IDBObjectStore>();
  const [page, setPage] = useState<PageType>('swipe');
  useEffect(() => {
    if (!isLoading) {
      const loadingPage = document.getElementById('loading');
      if (loadingPage)
        loadingPage.style.display = 'none';
    }
  }, [isLoading])

  /* connect to database, if profile exists retrieve it, otherwise render CreateProfile */
  useEffect(() => {
    const request = window.indexedDB.open(dbName, dbVersion);
    console.log(request);
    /* DB not supported or error occurred */
    request.onerror = (event: Event) => {
      return (
        <ErrorPage />
      )
    }
    /* DB doesn't exist, create DB and request.onsuccess event fires. */
    request.onupgradeneeded = (event: Event) => {
      console.log('onupgradeneeded event fired!');
      const db = (event.target as IDBOpenDBRequest).result;
      db?.createObjectStore(objectStoreName);
      setHasProfile(false);
    }
    /* DB exists */
    request.onsuccess = (event: Event) => {
      console.log('onsuccess event fired!');

      const db = (event.target as IDBOpenDBRequest).result;
      const transaction = db?.transaction(objectStoreName, "readwrite");
      const objectStore = transaction?.objectStore(objectStoreName);
      if (objectStore)
        setObjectStore(objectStore);
      if (hasProfile) {
        const getAllRequest = objectStore.getAll();

        getAllRequest.onsuccess = () => {
          const allData = getAllRequest.result;
          if (allData.length > 1) {
            console.error('multiple user profiles...');
          } else {
            setUserProfile(allData[0]);
          }
        };

        getAllRequest.onerror = () => {
          console.error("Error retrieving data:", getAllRequest.error);
        };
      }
    }

  }, [])


  return (
    <div  style={{display: 'flex', justifyContent: 'center'}}>
      <OuterContainer setLoading={setLoading} width={width}>
        { hasProfile ? 
        <CreateProfile setUserProfile={setUserProfile} objectStore={objectStore} /> :
        pages[page] }
        <NavBar setPage={setPage} width={width} />
      </OuterContainer>
    </div>
  )
}

export default App;
